// src/ArduinoController.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ArduinoController = () => {
    const [message, setMessage] = useState('');
    const [response, setResponse] = useState('');
    const [status, setStatus] = useState('');
    const [lastMessage, setLastMessage] = useState(''); // État pour le dernier message

    const handleSendMessage = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:3000/send', { message });
            setResponse(res.data);
        } catch (error) {
            console.error('Erreur:', error);
            setResponse('Erreur lors de l\'envoi du message.');
        }
    };

    const fetchStatus = async () => {
        try {
            const res = await axios.get('http://localhost:3000/status');
            setStatus(res.data);
        } catch (error) {
            console.error('Erreur:', error);
            setStatus('Erreur lors de la récupération de l\'état.');
        }
    };

    // Fonction pour récupérer le dernier message
    const fetchLastMessage = async () => {
        try {
            const res = await axios.get('http://localhost:3000/last-message');
            setLastMessage(res.data); // Mettez à jour l'état avec le dernier message
        } catch (error) {
            console.error('Erreur:', error);
            setLastMessage('Erreur lors de la récupération du dernier message.');
        }
    };

    useEffect(() => {
        const interval = setInterval(() => {
            fetchStatus();
            fetchLastMessage(); // Appelez fetchLastMessage pour obtenir le dernier message
        }, 2000);
        return () => clearInterval(interval); // Nettoyer l'intervalle à la désinscription
    }, []);

    return (
        <div>
            <h1>Contrôler le Coffre Arduino</h1>
            <form onSubmit={handleSendMessage}>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Entrez votre message"
                    required
                />
                <button type="submit">Envoyer</button>
            </form>
            <div id="response">{response}</div>
            <div id="status">État du coffre: {status}</div>
            <div id="lastMessage">Dernier message Arduino: {lastMessage}</div> {/* Afficher le dernier message */}
        </div>
    );
};

export default ArduinoController;
